INSERT INTO `global_settings` (`id`, `junior_licence_price`, `media_annual`, `media_single_event`, `under_maintenance`, `renewal_email_text`, `gift_voucher_content`, `voucher_amount`, `voucher_type`, `voucher_text`, `voucher_delay`, `voucher_email_disable`, `licence_global_junior_price`, `vehicle_passport_information_pdf`, `new_signup_email_subject`, `new_signup_email_content`, `maintenance_message`, `encrypted_key`, `stripe_publishable_key`, `stripe_secret_key`, `online_booking_fee`, `invoice_name`, `invoice_address`, `invoice_phone`, `invoice_email`, `invoice_abn`, `expiry_period_event_licence`, `invoice_footer_text`, `licence_application_declaration`, `agreement_protection_of_participants`, `medical_certificate`, `unlink_junior_account_email_subject`, `unlink_junior_account_email_content`, `profile_picture_rejected_email_notification_subject`, `profile_picture_rejected_email_notification_content`, `password_recovery_email_content`, `password_recovery_email_subject`, `proof_of_identity_rejected_email_notification_subject`, `proof_of_identity_rejected_email_notification_content`, `licence_renewal_email_subject`, `licence_renewal_email_content`, `club_membership_email_subject`, `club_membership_email_content`, `event_licence_what_happens_next`, `postmark_apikey`) VALUES
(1, 35, 100, 25, 2, '<div style=\"width:600px;max-width:100%;\">\r\n<p>Dear {Name} {Last Name},</p>\r\n\r\n<p>Your {Licence Name} is due for renewal. Please follow the link below to login to the User Portal to renew your licence before the expiry date. Once logged in, <strong>click the renewal link as displayed in the alert on your dashboard to renew.</strong></p>\r\n\r\n<p>If your licence is not renewed before the renewal date, your licence will expire and you will not be covered.</p>\r\n\r\n<p><strong>Renewal Date:</strong> {Renewal-Date}</p>\r\n\r\n<p><a href=\"https://sterlinginfotech.com/login\">Click here to login to the Sterlingin User Portal</a></p>\r\n\r\n<h3><br />\r\nAUSTRALIAN AUTO-SPORT ALLIANCE PTY. LTD.</h3>\r\n\r\n<p>PO Box 483<br />\r\nWangaratta VIC&nbsp;3677<br />\r\nAustralia.</p>\r\n\r\n<p><b>Phone:</b> (03) 5721 7800<br />\r\n<b>Email:</b> <a href=\"mailto:info@sterlinginfotech.com\" target=\"_blank\">info@sterlinginfotech.com</a><br />\r\n<b>Web:</b> <a href=\"http://www.sterlinginfotech.com/\" target=\"_blank\">www.sterlinginfotech.com</a></p>\r\n</div>\r\n', '<p>Hi&nbsp;{Name},</p>\r\n\r\n<p>Congratulations!&nbsp;{Purchaser Name} has sent you a Sterlingin gift voucher to the value&nbsp;of {Value}!</p>\r\n\r\n<p>In order to redeem your gift voucher, simply select the&nbsp;licence that would like to apply for, and then enter the code below in the coupon section of the entry form.</p>\r\n\r\n<h2><strong>Voucher Code:</strong>&nbsp;{Voucher Code}</h2>\r\n\r\n<p>This voucher will expire on:&nbsp;{Expiry Date}</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 26, 1, '<p>Voucher Code : {Voucher Code}</p>\r\n\r\n<p>Value : {Value}</p>\r\n', 3, 0, '1', '170714__Vehicle_Passport_Information_2017_V5.pdf', 'Welcome to Sterlingin NSW!! :)', '<p>Dear {Name},</p>\r\n\r\n<p>Thank you for creating a profile on the <em>all new </em>MySterlingin Portal. Your Member ID is<strong>&nbsp;{Member-ID}</strong></p>\r\n\r\n<p>You can use MySterlingin Portal to manage your Sterlingin NSW profile, purchase Club Memberships, apply for Sterlingin NSW Licences as well as enter Events.</p>\r\n\r\n<p>To reset your password, please use the link below.<br />\r\n{Reset-Link}</p>\r\n\r\n<p><strong>Date of signup:</strong> {Singup-Date}</p>\r\n\r\n<p>If you have any questions, our friendly office staff are available 9am to 5pm Monday to Friday on 02 4731 5000.</p>\r\n\r\n<p>Kind Regards<br />\r\nThe Team at Sterlingin NSW</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.sterlinginfotech.com/wp-content/uploads/2021/06/KNSW-Circle-Logo_100px.png\" style=\"width: 100px; height: 100px;\" /></p>', '<p><label class=\"col-sm-12 control-label\">Under Maintenance </label><br />\r\n&nbsp;</p>\r\n', '213c7c691e91b8cde79f9546d5d57640', 'pk_test_578WanK7SzdvJTw5jt0FCdmQ', 'ZzZTTzZqbTBBb0JsVEEzdm9Dd0lrMkhnSzk3elFWMkpKTHhVM1ZWTWZoeDBNY1pYbnJtUjJyeTVDbDkvTDlrOA==', 1.95, 'Sterling Incorporated', 'Sterling Infotech, Durgapura Jaipur 302018', '9468590655', 'admin@sterlinginfotech.com', '60 012 024 854', 25, NULL, '<p>WARNING! THIS IS AN IMPORTANT DOCUMENT WHICH AFFECTS YOUR LEGAL RIGHTS AND OBLIGATIONS, PLEASE READ IT CAREFULLY AND DO NOT AGREE TO IT UNLESS YOU ARE SATISFIED YOU UNDERSTAND IT.<br />\r\nTHIS AGREEMENT IS BETWEEN YOURSELF LICENCE HOLDER) AND THE KARTING (NEW SOUTH WALES) INC (KNSW). IN CONSIDERATION OF BEING PERMITTED TO ENTER KNSW AUTHORISED VENUES AND PARTICIPATE IN RECREATIONAL ACTIVITIES CONDUCTED AT THE KNSW AUTHORISED VENUE, IN THE CAPACITY OF THE LICENCE HOLDER ACKNOWLEDGES AND AGREES:<br />\r\n1. KNSW is in the business of providing recreational services that relate to the sport of go sterling, including official and private practice, come and try days, demonstrations, displays and race competition(&ldquo;Business&rdquo;).<br />\r\n2. By entering the KNSW Authorised Venue the Licence Holder is at risk of death or of suffering personal injury (both physical and psychological) or loss and damage to property (&ldquo;Harm&rdquo;) arising from KNSW conducting its Business.<br />\r\n3. Possible causes of Harm include, but are not limited to: being hit by a go kart or debris from a go kart, a go kart crashing into another go kart (including your go kart if you are a driver), a go kart crashing into safety barriers and fencing or driving off the circuit, debris on the circuit, you or another slipping and falling at the KNSW Authorised Venue, mechanical failure of a go kart or failure of a structure installed on the KNSW Authorised Venue, acts of violence or other harmful acts (whether intentional or inadvertent) committed by others at the KNSW Authorised Venue.<br />\r\n4. The Licence Holder releases and indemnifies KNSW and holds it harmless with respect to all liability for my death, personal injury and all other loss and damage, including damage to my property howsoever arising, except to the extent prohibited by law.<br />\r\n5. The Licence Holder voluntarily entered the KNSW Authorised Venue at its own risk and knows that go sterling is a potentially dangerous activity. The Licence Holder will not, whilst on the premises, consume or otherwise take in substances or do things that will adversely affect the Licence Holder&rsquo;s physical or mental ability, capacity or judgement.<br />\r\n6. If the Licence Holder participates at the KNSW Authorised Venue in the capacity of a driver or pit crew, the Licence Holder declares that it has read and understood the KNSW Rules and the Supplementary Regulations issued for the day.<br />\r\n7. The Licence Holder has disclosed on this form any medical condition which may affect the Licence Holder&rsquo;s physical or mental ability, capacity or judgement.<br />\r\n8. KNSW reserves the right to refuse the Licence Holder, without limitation, entry to the KNSW Authorised Venue and at any time may with draw its consent for the Licence Holder to remain at the KNSW Authorised Venue.<br />\r\n9. Before signing this form the Licence Holder has read and fully understands its meaning and that the Licence Holder, by signing this form, may be giving up important legal rights.<br />\r\nWARNING&ndash;AUSTRALIAN CONSUMER LAW AND OTHER LEGISLATION<br />\r\nUnder the Australian Consumer Law, several statutory guarantees apply to the supply of certain goods and services. These guarantees mean that the supplier named on this form is required to ensure that the recreational services it supplies to you are rendered with due care and skill; and are reasonably fit for any purpose which you, either expressly or by implication, make known to the supplier; and might reasonably be expected to achieve any result you have made known to the supplier.<br />\r\nUnder the Australian Consumer Law and in some states, other legislation, the supplier is entitled to ask you to agree that these statutory guarantees do not apply to you. If you sign this form, you will be agreeing that your rights to sue the supplier under the Australian Consumer Law and other legislation if you are killed or injured because the services provided were not in accordance with these guarantees, are excluded, restricted or modified in the way set out in this form.<br />\r\nNOTE: The change to your rights, as set out in this form, does not apply if your death or injury is due to gross negligence on the supplier&rsquo;s part. Gross negligence, in relation to an act or omission, means doing the act or omitting to do an act with reckless disregard, with or without consciousness, for the consequences of the act or omission.<br />\r\nBy checking this box I signal my agreement with the Declaration.</p>', '<div style=\"margin-left:0pt;\">\r\n<ul>\r\n	<li>I (and/or my participating child) am/are <strong>in good health </strong>and in proper physical condition to safely participate in the KNSW Sanctioned Event. I certify that I (and/or my participating child) have <strong>no known or</strong> <strong>knowable physical or mental conditions </strong>that would affect my/our ability to safely participate in the KNSW Sanctioned Event, or that would result in my/our participation creating a risk of danger to myself or to others.</li>\r\n	<li>I acknowledge that Sterlingin NSW recommends and <strong>encourages each participant to get medical clearance </strong>from his/her personal physician prior to participation.</li>\r\n	<li>I (and/or my participating child) <strong>have not been advised or cautioned against participating </strong>by a medical practitioner.</li>\r\n	<li>I understand that it is my responsibility to continuously <strong>monitor my own (and/or my participating child&rsquo;s) physical and mental condition during the Event</strong>, and I agree to withdraw immediately and to notify officials if at any point my/our continued participation would create a risk of danger to myself or to others.</li>\r\n	<li>In the event of an injury to me that renders me (and/or my participating child) unconscious or incapable of making a medical decision, <strong>I authorise and give my consent to KNSW and emergency or medical personnel at the event to make emergency medical </strong>decisions (including providing emergency medical treatment) on my (and/or my participating child&rsquo;s) behalf (including but not limited to CPR).</li>\r\n	<li>I authorise KNSW to <strong>secure emergency medical care or transportation </strong>when reasonably necessary (at my cost and expense).</li>\r\n	<li>I acknowledge that KNSW has instituted its <strong>rules for the protection </strong>of the participant and co-participants. I (and/or my participating child) agree to familiarise myself/ourselves with those rules and follow them at all relevant times. I also recognise the authority of KNSW to halt my/our participation (and, if necessary, have me removed from the premises) if my/our participation, conduct, or presence endangers myself or my coparticipants.</li>\r\n	<li>Alcohol &amp; Drugs: I certify that I am not, and on the date of the KNSW Sanctioned Event will not be, under the influence of alcohol or any drugs that would in any way impair my ability to safely participate in the KNSW Sanctioned Event. I understand that the consumption of any alcohol and any illicit or illegal drugs or substances during the event is strictly prohibited.</li>\r\n</ul>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin-left:0pt;\">NOTICE OF FILMING &amp; PHOTOGRAPHY AND/OR AUDIO RECORDINGS</p>\r\n\r\n<ul>\r\n	<li>You acknowledge that you may be photographed and/or filmed at the Event. Sterlingin NSW Officials and Authorised Photographers may create, record, transmit, reproduce and use visual and audio-visual footage (&ldquo;Recordings&rdquo;) and still images (&ldquo;Images&rdquo;) of you at or in relation to the Event.</li>\r\n	<li>You grant Sterlingin NSW Officials and Authorised Photographers permission to use Recordings and Images in any media, worldwide and for any purpose without identification, compensation or payment of any kind.</li>\r\n	<li>If you do not wish to be photographed or filmed for these purposes, you must advise the organisers of the event.</li>\r\n</ul>', 'Medical_Form.pdf', 'MySterlingin Portal Profile removed from Family Link', '<p>Dear {Name},</p>\r\n\r\n<p>You have been removed from your parents Family Link account in MySterlingin Portal. This means your Parent / Guardian can no longer access your profile, update your membership, renew your licence, or enter you into Events. You now have your own account independent of any other family members.&nbsp;</p>\r\n\r\n<p>Member ID:&nbsp;{Member-ID}</p>\r\n\r\n<p>To reset your password, please use the link below.<br />\r\n{Reset-Link}</p>\r\n\r\n<p>If you have any questions, our friendly office staff are available 9am to 5pm Monday to Friday on 02 4731 5000.</p>\r\n\r\n<p>Kind Regards<br />\r\nThe Team at Sterlingin NSW</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.sterlinginfotech.com/wp-content/uploads/2021/06/KNSW-Circle-Logo_100px.png\" style=\"width: 100px; height: 100px;\" /></p>', 'Uh-oh. Your Sterlingin NSW Profile Photo was rejected :(', '<p>Dear&nbsp;{Name},</p>\r\n\r\n<p>Member ID:&nbsp;{Member-ID}</p>\r\n\r\n<p>You recently submitted a Profile Photo to Sterlingin NSW through the <em>all new </em>MySterlingin Portal. High quality Profile Photos are important as they are used by Race Secretaries, Officials and Administrators to confirm your identity. Unfortunately, Sterlingin NSW could not accept your Profile Photo.&nbsp;</p>\r\n\r\n<p>Note:</p>\r\n\r\n<ul>\r\n	<li>Your Profile Photo is <u>different</u> to your Proof of Identity.</li>\r\n	<li>A Profile Photo can be a photo of yourself taken on a mobile phone&nbsp;or other personal device.</li>\r\n	<li>A Proof of Identity is a photo of your&nbsp;Birth Certificate, Passport, Drivers Licence or other Government issued Photo ID.&nbsp;For security reasons, Proof of Identity photos are not stored online following approval by Sterlingin NSW.&nbsp;&nbsp;</li>\r\n</ul>\r\n\r\n<p><strong>Action Required: </strong>Please logon to the <em>all new&nbsp;</em>MySterlingin Portal and re-submit a profile photo of yourself that:</p>\r\n\r\n<ul>\r\n	<li>clearly shows your face</li>\r\n	<li>doesn&#39;t not contain sunglasses</li>\r\n	<li>could be used to validate the same person in the Profile Photo matches the same person in your Proof of Identity.</li>\r\n</ul>\r\n\r\n<p>If you have any questions, our friendly office staff are available 9am to 5pm Monday to Friday on 02 4731 5000.</p>\r\n\r\n<p>Kind Regards<br />\r\nThe Team at Sterlingin NSW</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.sterlinginfotech.com/wp-content/uploads/2021/06/KNSW-Circle-Logo_100px.png\" style=\"width: 100px; height: 100px;\" /></p>', '<p>Dear {Name},</p>\r\n\r\n<p>Thank you for using MySterlingin Portal.&nbsp;</p>\r\n\r\n<p>Member ID:&nbsp;{Member-ID}</p>\r\n\r\n<p>Please click the URL below to recover your password:<br />\r\n{Password Recovery URL}</p>\r\n\r\n<p>If you still have problems logging in, please log Support Ticket via&nbsp;<a href=\"https://www.sterlinginfotech.com/support/\" target=\"_blank\">www.sterlinginfotech.com/support</a>. Alternatively if the matter is urgent,&nbsp;our friendly office staff are available 9am to 5pm Monday to Friday on 02 4731 5000.</p>\r\n\r\n<p>Kind Regards<br />\r\nThe Team at Sterlingin NSW</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.sterlinginfotech.com/wp-content/uploads/2021/06/KNSW-Circle-Logo_100px.png\" style=\"width: 100px; height: 100px;\" /></p>', 'MySterlingin Portal Password Recovery', 'Uh-oh. Your Sterlingin NSW Proof of Identity was rejected :(', '<p>Dear {Name},</p>\r\n\r\n<p>Member ID:&nbsp;{Member-ID}</p>\r\n\r\n<p>You recently submitted your Proof of Identity (POI) to Sterlingin NSW through the&nbsp;<em>all new&nbsp;</em>MySterlingin Portal. Your Proof of Identity is important as it&#39;s used by Race Secretaries, Officials and Administrators to confirm your identity. Unfortunately, Sterlingin NSW could not accept your Proof of Identity.</p>\r\n\r\n<p>Note:</p>\r\n\r\n<ul>\r\n	<li>Your Proof of Identity is&nbsp;<u>different</u>&nbsp;to your Profile Pic.</li>\r\n	<li>A Profile Photo can be a photo of yourself taken on a mobile phone&nbsp;or other personal device.</li>\r\n	<li>A Proof of Identity is a photo of your&nbsp;Birth Certificate, Passport, Drivers Licence or other Government issued Photo ID.&nbsp;For security reasons, Proof of Identity photos are not stored online following approval by Sterlingin NSW.&nbsp;&nbsp;&nbsp;</li>\r\n</ul>\r\n\r\n<p><strong>Action Required:&nbsp;</strong>Please logon to the&nbsp;<em>all new&nbsp;</em>MySterlingin Portal and re-submit your Proof of Identity:</p>\r\n\r\n<ul>\r\n	<li>Birth Certificate or Passport (for Children)</li>\r\n	<li>Passport, Drivers Licence or other Government issued Photo ID (for Adults)</li>\r\n</ul>\r\n\r\n<p>If you have any questions, our friendly office staff are available 9am to 5pm Monday to Friday on 02 4731 5000.</p>\r\n\r\n<p>Kind Regards<br />\r\nThe Team at Sterlingin NSW</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.sterlinginfotech.com/wp-content/uploads/2021/06/KNSW-Circle-Logo_100px.png\" style=\"width: 100px; height: 100px;\" /></p>', 'Reminder: Licence Renewal', '<p>Dear {Name},</p>\r\n\r\n<p>This is just reminder that you have a Sterlingin NSW Licence that needs to be renewed.</p>\r\n\r\n<p>Member ID:&nbsp;{Member-ID}<br />\r\nLicence Type: {Licence Type}<br />\r\nLicence Expiry Date: {Renewal Date}</p>\r\n\r\n<p>Please click here to renew your Licence {Renewal URL}</p>\r\n\r\n<p>If you have any questions, our friendly office staff are available 9am to 5pm Monday to Friday on 02 4731 5000.</p>\r\n\r\n<p>Kind Regards<br />\r\nThe Team at Sterlingin NSW</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.sterlinginfotech.com/wp-content/uploads/2021/06/KNSW-Circle-Logo_100px.png\" style=\"width: 100px; height: 100px;\" /></p>', 'Reminder: Club Membership Renewal', '<p>Dear {Name},</p>\r\n\r\n<p>This is just reminder that you need to renew your Club Membership:</p>\r\n\r\n<p>Club Name: {Club Name}<br />\r\nMember ID:&nbsp;{Member-ID}<br />\r\nClub Expiry Date: {Renewal Date}</p>\r\n\r\n<p>Click here to renew your {Club Name} Membership {Renewal URL}</p>\r\n\r\n<p>If you have any questions, our friendly office staff are available 9am to 5pm Monday to Friday on 02 4731 5000.</p>\r\n\r\n<p>Kind Regards<br />\r\nThe Team at Sterlingin NSW</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.sterlinginfotech.com/wp-content/uploads/2021/06/KNSW-Circle-Logo_100px.png\" style=\"width: 100px; height: 100px;\" /></p>', NULL, NULL);
