--
-- Table structure for table `licence_payments`
--

CREATE TABLE `licence_payments` (
  `id` bigint(30) NOT NULL,
  `licence_id` int(11) DEFAULT NULL,
  `user_id` bigint(30) DEFAULT NULL,
  `grade_id` int(4) DEFAULT NULL,
  `karting_event_id` bigint(30) DEFAULT NULL,
  `invoice_number` varchar(150) DEFAULT NULL,
  `invoice_number_numeric` bigint(30) DEFAULT NULL,
  `total_amount` float DEFAULT NULL COMMENT 'Total amount without discount',
  `voucher_code` varchar(150) DEFAULT NULL,
  `voucher_discount` float DEFAULT NULL,
  `online_booking_fee` float DEFAULT NULL,
  `amount` float DEFAULT NULL,
  `added_by` int(11) DEFAULT NULL COMMENT 'admin id who added licence',
  `kids_api_transaction_code` varchar(255) DEFAULT NULL,
  `kart_start_licence_payment_id` int(11) DEFAULT NULL,
  `renewal_date` date DEFAULT NULL,
  `renew` int(11) DEFAULT NULL,
  `parent_payment_id` bigint(30) DEFAULT NULL,
  `stripe_id` varchar(255) DEFAULT NULL,
  `date_time` datetime DEFAULT NULL,
  `status` int(3) DEFAULT NULL COMMENT '1= Active, 3= Expired, 5 = Suspended,\r\n7= Renewed\r\n15 = used in event'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

