INSERT INTO `licence_settings` (`id`, `licence_type`, `subject`, `content`, `licence_price`, `current_status`, `minimum_age`, `maximum_age`, `licence_maintainance_message`, `renewable_licence`, `renewal_period`, `licence_description`, `safetly_video_url`, `hide_safetly_video`, `approved_email_subject`, `approved_email_content`, `pending_email_subject`, `panding_email_content`, `denied_email_subject`, `denied_email_content`, `suspended_email_subject`, `suspended_email_content`, `converted_email_subject`, `converted_email_content`) VALUES
(1, 1, 'Novice Licence Success - You\'re ready to hit the track!', '<p>Dear {Name}</p>\r\n\r\n<p>Congratulations on obtaining a Novice Competition Licence. Your Sterling Infotech licence number is&nbsp;<strong>{member-number}.</strong></p>\r\n\r\n<p>We strongly recommend you speak with your club or a legitimate&nbsp;kart shop before purchasing a go kart, protective equipment or transponder.</p>\r\n\r\n<p><strong>IMPORTANT</strong>: If you have not raced before, you will need to obtain a Driving Test Endorsement. This can be arranged by contacting your club prior to your first race meeting.</p>\r\n\r\n<p>It is important for all Competition Licence holders to become familiar with the&nbsp;<a href=\"https://sterlinginfotech.com/rules-regulations/\" target=\"_blank\">Sterling Infotech Rules,</a>&nbsp;<a href=\"https://sterlinginfotech.com/technical-specifications/\" target=\"_blank\">Technical Specifications</a>, and <a href=\"https://sterlinginfotech.com/forms-policies/\">Policies</a> on the Sterling Infotech website.</p>\r\n\r\n<p>If you have any questions your club cannot assist with, our friendly office staff are available 9am to 5pm Monday to Friday on 02 4731 5000.</p>\r\n\r\n<p>Kind Regards<br />\r\nThe Team at Sterling Infotech</p>', 230, 2, 6, 10, '<p>Novice Licence Down for Maintenance</p>', 1, 1, '<div class=\"col-lg-12 normal\">\r\n<p>Allows 7 to 10 year olds to compete in Club, Zonal, State and National events sanctioned by Sterling Infotech.</p>\r\n\r\n<p>Allows 6 to 10 year olds to practice at Sterling Infotech affiliated clubs.</p>\r\n\r\n<p>To apply for this licence you will need to complete your:</p>\r\n\r\n<ul>\r\n	<li>Proof of Identity. For example:&nbsp;Birth Certificate, Passport, Drivers Licence and/or other Government issued Photo ID.</li>\r\n	<li>Profile Picture clearly showing your face.</li>\r\n	<li>Medical Declaration.</li>\r\n	<li>Plus have a current Club Membership.</li>\r\n</ul>\r\n</div>', 'https://www.youtube.com/embed/GpTiMJ3aS4M', 1, NULL, NULL, 'Novice Licence Application Received - Under Review', '<p>Dear {Name}</p>\r\n<p>Thank you for submitting your Novice Competition Licence application. Your application is currently under review and your reference number is {member-number}.</p>\r\n<p>We aim to process all applications within 5-7 business days. You will receive an email notification once a decision has been made.</p>\r\n\r\n<p>If you have any questions, our friendly office staff are available 9am to 5pm Monday to Friday on 02 4731 5000.</p>\r\n<p>Kind Regards<br/>\r\nThe Team at Karting NSW</p>', 'Novice Licence Application Update - Denied', '<p>Dear {Name}</p>\r\n<p>Thank you for your interest in obtaining a Novice Competition Licence. Unfortunately, we are unable to approve your application at this time. Your reference number is {member-number}.</p>\r\n<p>Reason for decision: {denial-reason}</p>\r\n\r\n<p>If you believe this decision has been made in error or you would like to discuss your options, please contact our office. In some cases, additional documentation or information may allow us to reconsider your application.</p>\r\n\r\n<p>If you have any questions, our friendly office staff are available 9am to 5pm Monday to Friday on 02 4731 5000.</p>\r\n<p>Kind Regards<br/>\r\nThe Team at Karting NSW</p>', NULL, NULL, NULL, NULL),
(2, 2, 'Rookie Licence Success - You\'re ready to hit the track!', '<p>Dear {Name}</p>\r\n\r\n<p>Congratulations on obtaining a Rookie Competition Licence. Your Sterling Infotech licence number is&nbsp;<strong>{member-number}</strong><strong>.</strong></p>\r\n\r\n<p>We strongly recommend you speak with your club or a legitimate&nbsp;kart shop before purchasing a go kart, protective equipment or transponder.</p>\r\n\r\n<p><strong>IMPORTANT</strong>: If you have not raced before, you will need to obtain a Driving Test Endorsement. This can be arranged by contacting your club prior to your first race meeting.</p>\r\n\r\n<p>It is important for all Competition Licence holders to become familiar with the&nbsp;<a href=\"https://sterlinginfotech.com/rules-regulations/\" target=\"_blank\">Sterling Infotech Rules,</a>&nbsp;<a href=\"https://sterlinginfotech.com/technical-specifications/\" target=\"_blank\">Technical Specifications</a>, and&nbsp;<a href=\"https://sterlinginfotech.com/forms-policies/\">Policies</a>&nbsp;on the Sterling Infotech website.</p>\r\n\r\n<p>If you have any questions your club cannot assist with, our friendly office staff are available 9am to 5pm Monday to Friday on 02 4731 5000.</p>\r\n\r\n<p>Kind Regards<br />\r\nThe Team at Sterling Infotech<br />\r\n<img src=\"https://sterlinginfotech.com/wp-content/uploads/2021/06/KNSW-Circle-Logo_100px.png\" /></p>', 225, 2, 9, 13, '', 1, 1, '<p>Allows 9&nbsp;to 13&nbsp;year olds to compete in Club, Zonal, State and National events sanctioned by Sterling Infotech.</p>\r\n\r\n<p>Allows 9&nbsp;to 13&nbsp;year olds to practice at Sterling Infotech affiliated clubs.</p>\r\n\r\n<p>To apply for this licence you will need to complete your:</p>\r\n\r\n<ul>\r\n	<li>Proof of Identity. For example:&nbsp;Birth Certificate, Passport, Drivers Licence and/or other Government issued Photo ID.</li>\r\n	<li>Profile Picture clearly showing your face.</li>\r\n	<li>Medical Declaration.</li>\r\n	<li>Plus have a current Club Membership.</li>\r\n</ul>', 'https://www.youtube.com/embed/GpTiMJ3aS4M', 1, NULL, NULL, 'Rookie Licence Application Received - Under Review', '<p>Dear {Name}</p>\r\n<p>Thank you for submitting your Rookie Competition Licence application. Your application is currently under review and your reference number is {member-number}.</p>\r\n<p>We aim to process all applications within 5-7 business days. You will receive an email notification once a decision has been made.</p>\r\n\r\n<p>If you have any questions, our friendly office staff are available 9am to 5pm Monday to Friday on 02 4731 5000.</p>\r\n<p>Kind Regards<br/>\r\nThe Team at Karting NSW</p>', 'Rookie Licence Application Update - Denied', '<p>Dear {Name}</p>\r\n<p>Thank you for your interest in obtaining a Rookie Competition Licence. Unfortunately, we are unable to approve your application at this time. Your reference number is {member-number}.</p>\r\n<p>Reason for decision: {denial-reason}</p>\r\n\r\n<p>If you believe this decision has been made in error or you would like to discuss your options, please contact our office. In some cases, additional documentation or information may allow us to reconsider your application.</p>\r\n\r\n<p>If you have any questions, our friendly office staff are available 9am to 5pm Monday to Friday on 02 4731 5000.</p>\r\n<p>Kind Regards<br/>\r\nThe Team at Karting NSW</p>', NULL, NULL, NULL, NULL),
